mapper.select:
  		SELECT GOOD
	  		FROM GREAT
	  		JOIN RESTAURANT ON(REST_NUM=GREAT_RESTNUM)
	  		JOIN MEMBER ON(USER_NUM=GREAT_USERNUM)
  		WHERE GREAT_RESTNUM = #{restNum}
  		  AND GREAT_USERNUM = #{userNum}

  		SELECT GOOD
		FROM GREAT
		WHERE GREAT_RESTNUM = #{restNum}
		AND GREAT_USERNUM = #{userNum}

mapper.update:
UPDATE RESTAURANT
SET GRADE = #{grade}
WHERE REST_NUM = #{restNum}
