mapper.update:
  	UPDATE REVIEW
			SET STATUS = 'N'
		WHERE REV_RESTNUM = #{revRestnum}
  		AND MEMBER_NUM = #{memberNum}

mapper.select:
SELECT *
FROM REVIEW
WHERE REV_RESTNUM = #{revRestnum}
AND MEMBER_NUM = #{memberNum}
AND STATUS = 'Y'

  		SELECT ROUND(AVG(REV_AVGSCORE)) AS AVG_SCORE
		FROM REVIEW
		WHERE REV_RESTNUM = #{restNum}
