mapper.update:
UPDATE MEMBER
	SET EMAIL = #{email}
WHERE USER_ID = #{userId}

UPDATE MEMBER
	SET NICKNAME = #{nickname}
WHERE USER_ID = {userId}

UPDATE MEMBER
	SET PHONE = #{phone}
WHERE USER_ID = #{userId}

UPDATE MEMBER
	SET PASSWORD = #{password}
WHERE USER_ID = #{userId}

mapper.select:
SELECT USER_ID 
FROM MEMBER
WHERE NAME = #{name} AND EMAIL = #{email}
